    public void ItemCost()
    {
        double sum =0;
        
        for(int i = 0; i <producttable.getRowCount(); i++)
        {
            sum = sum + Double.parseDouble(producttable.getValueAt(i, 2).toString());
        }
      
        
        String iTotal = String.format(" %.2f", sum);
        totaltxt.setText(iTotal);
        
        String BarCode = String.format("Total is %.2f", iTotal);
        jtxtBarCode.setText(BarCode);
    }
    
    //====================Functiion Change======================
    
    public void Balance() {
        double total = Double.parseDouble(totaltxt.getText());

        double pay = Double.parseDouble(paytxt.getText());

        double bal = pay - total;

        String dbal = String.format("%.2f", bal);
        balancetxt.setText(dbal);
    }
    
  
            
