    private void paytxtActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_paytxtActionPerformed
        // TODO add your handling code here:
        try {
            if ((totaltxt.getText().length() == 0) || (paytxt.getText().length() == 0)) {

                JOptionPane.showMessageDialog(this, "Please Input Payment or Sales!");
            } else {
                double total = Double.parseDouble(totaltxt.getText());
                double pay = Double.parseDouble(paytxt.getText());
                if (total <= pay) {

                    Balance();
                    JOptionPane.showMessageDialog(this, "Sales Completed!");

                } else {
                    JOptionPane.showMessageDialog(this, "Insufficient Cash!");
                }
            }
        } catch (Exception ex) {
            JOptionPane.showMessageDialog(this, "Invalid Input!");
        }
    }//GEN-LAST:event_paytxtActionPerformed

